# moodle users view
CREATE VIEW `v_moodle_users` AS (select `users`.`id` AS `id`,concat('n',`users`.`id`) AS `moodle_id`,`users`.`email` AS `email`,`users`.`first_name` AS `first_name`,`users`.`last_name` AS `last_name`,`users`.`password` AS `password`,`users`.`moodle_token` AS `moodle_token`,`clients`.`moodle_course_id` AS `moodle_course_id`,`clients`.`name` AS `client_name` from (`users` left join `clients` on((`users`.`client_id` = `clients`.`id`))));

